        };
        public QuizResults(string username, string password,string theme,string test, Dictionary<string,int> jobs)
        {
            InitializeComponent();
            this.username = username;
            this.password = password;
            this.jobs = jobs;
            this.theme = theme;
            this.test = "yes";
            foreach (var key in jobs.Keys)
            {
                //o κανονικός τυπος είναι S(E)/M(E)*100 αλλά το S(E)=jobs[key], to M(E)=max_scores[key]
                maxPossibleM = max_scores.Values.Max(); //ο μαξ βαθμος από το max_scores , που είναι 31
                score = (float)(jobs[key]) / max_scores[key] * 100 * ((float)max_scores[key] / maxPossibleM);  // ==> ποσοστό ταιριάσματος με τους συντελεστές βαρύτητας
                match_jobs[key] = score;
            }

        }   


       

        private void button2_Click(object sender, EventArgs e)
        {
            //αποθηκεύω τα αποτελέσματα τις πίτας σαν εικόνα τοπικά στον υπολογιστή μου
            using (SaveFileDialog sfd = new SaveFileDialog())
            {
                sfd.Filter = "PNG Image|*.png|JPEG Image|*.jpg|Bitmap Image|*.bmp";
                sfd.Title = "Αποθήκευση Γραφήματος";
                sfd.FileName = "Chart.png";

                if (sfd.ShowDialog() == DialogResult.OK)
                {
                    // αποθηκεύει την εικόνα στο επιλεγμένο path
                    chart1.SaveImage(sfd.FileName, ChartImageFormat.Png);
                    MessageBox.Show("Το γράφημα αποθηκεύτηκε επιτυχώς!", "Επιτυχία", MessageBoxButtons.OK, MessageBoxIcon.Information);
                }
            }
        }

        private void QuizResults_Load(object sender, EventArgs e)
        {
            if (theme=="light")
            {
                this.BackColor = Color.White;
                label1.ForeColor = Color.Black;
                chart1.ForeColor = Color.Black;
                chart1.BackColor = Color.White;
                button2.BackColor= Color.Silver;
                button2.ForeColor= Color.Black;
                chart1.ChartAreas[0].BackColor = Color.White;
                if (chart1.Legends.Count > 0)
                {
                    chart1.Legends[0].BackColor = Color.White;
                    chart1.Legends[0].ForeColor = Color.Black; 
                }
            }
            else if (theme=="dark")
            {
                this.BackColor = Color.FromArgb(64, 64, 64);
                label1.ForeColor = Color.White;
                chart1.ForeColor = Color.White;
                chart1.BackColor = Color.FromArgb(64, 64, 64);
                button2.BackColor = Color.DimGray;
                button2.ForeColor = Color.White;
                chart1.ChartAreas[0].BackColor = Color.FromArgb(64, 64, 64);
                if (chart1.Legends.Count > 0)
                {
                    chart1.Legends[0].BackColor = Color.FromArgb(64, 64, 64);
                    chart1.Legends[0].ForeColor = Color.White; 
                }



            }
            //εδώ θα φορτώνει η πίτα

            //tαξινομώ κατά φθίνουσα
           var sortedDict = match_jobs
    .OrderByDescending(kvp => kvp.Value)
    .ToDictionary(kvp => kvp.Key, kvp => kvp.Value);

            //ταξινομημένο που περιέχει μόνο τα 5 πρώτα
            var top5 = match_jobs
            .OrderByDescending(kvp => kvp.Value)
            .Take(5);

            // εμφάνιση επαγγελμάτων και ποσοστών πάνω στην πίτα
            chart1.Series["Επαγγέλματα"].ChartType = SeriesChartType.Pie;
            chart1.Series["Επαγγέλματα"].IsValueShownAsLabel = true;
            foreach (var kvp in top5)
            {
                int pointIndex = chart1.Series["Επαγγέλματα"].Points.AddY(kvp.Value);
                var point = chart1.Series["Επαγγέλματα"].Points[pointIndex];

                point.LegendText = $"{kvp.Key} ({kvp.Value:F2}%)"; //πάνω δεξιά
                point.Label = $"{kvp.Key}\n ({kvp.Value:F2}%)";   // πάνω στην πίτα

            }
            var firstItem = top5.First().Key; //παίρνω το επάγγελμα με το μεγαλύτερο σκορ
            label1.Text = firstItem.ToString() + "\n" +job_description[top5.First().Key]; //εμφανίζω την περιγραφή από το description

        }


        private void button2_MouseHover(object sender, EventArgs e)
        {
            toolTip.SetToolTip(button2, "Μορφές αποθήκευσης: .PNG*, .JPEG*, Bitmap");
        }
    }
